export default function App() {
  const [studentName, setStudentName] = useState('');
  const [hasStarted, setHasStarted] = useState(false);
  const [completedLessons, setCompletedLessons] = useState(new Set());
  const [activeLesson, setActiveLesson] = useState(null);
  const [fontSize, setFontSize] = useState('normal'); // 'normal' | 'large'

  useEffect(() => {
    const storedName = localStorage.getItem('copywritingJourneyStudentName');
    const storedProgress = localStorage.getItem('copywritingJourneyProgress');
    const storedFontSize = localStorage.getItem('copywritingJourneyFontSize');
    if (storedName) {
      setStudentName(storedName);
      setHasStarted(true);
    }
    if (storedProgress) {
      setCompletedLessons(new Set(JSON.parse(storedProgress)));
    }
    if (storedFontSize) {
        setFontSize(storedFontSize);
    }
  }, []);

  useEffect(() => {
    if (hasStarted) {
       localStorage.setItem('copywritingJourneyProgress', JSON.stringify(Array.from(completedLessons)));
    }
  }, [completedLessons, hasStarted]);


  const journeyRef = useRef(null);
  const { scrollYProgress } = useScroll({
    target: journeyRef,
    offset: ["start center", "end end"]
  });
  const lineHeight = useTransform(scrollYProgress, [0, 1], ["0%", "100%"]);
  
  const toggleFontSize = () => {
    const newSize = fontSize === 'normal' ? 'large' : 'normal';
    setFontSize(newSize);
    localStorage.setItem('copywritingJourneyFontSize', newSize);
  };

  const handleStartJourney = (name) => {
    localStorage.setItem('copywritingJourneyStudentName', name);
    setStudentName(name);
    setHasStarted(true);
  };
  
  const handleRestartJourney = () => {
      localStorage.removeItem('copywritingJourneyStudentName');
      localStorage.removeItem('copywritingJourneyProgress');
      localStorage.removeItem('copywritingJourneyFontSize');
      setStudentName('');
      setCompletedLessons(new Set());
      setHasStarted(false);
      setFontSize('normal');
  };

  const handleStartLesson = (lesson) => setActiveLesson(lesson);
  const handleCloseModal = () => setActiveLesson(null);

  const handleCompleteLesson = (lessonId) => {
    setCompletedLessons(prev => {
        const newSet = new Set(prev);
        newSet.add(lessonId);
        return newSet;
    });
    handleCloseModal();
  };

  const handleUncompleteLesson = (lessonId) => {
      setCompletedLessons(prev => {
          const newSet = new Set(prev);
          newSet.delete(lessonId);
          return newSet;
      });
      handleCloseModal();
  };
  
  const allLessonsCompleted = completedLessons.size === TOTAL_LESSONS;

  if (!hasStarted) {
    return (
        <div className="adventure-bg font-cairo">
            <GlobalStyles />
            <WelcomeScreen onStart={handleStartJourney} />
            <DesertFog />
        </div>
    );
  }

  return (
    <div className={`adventure-bg font-cairo text-[#3a2e25] ${fontSize === 'large' ? 'text-lg' : 'text-base'}`}>
      <GlobalStyles />
      <Header />
      <Navbar 
        studentName={studentName} 
        completedCount={completedLessons.size} 
        totalCount={TOTAL_LESSONS}
        toggleFontSize={toggleFontSize}
      />

      <main className="container mx-auto px-4 sm:px-6 lg:px-8 py-16 relative z-10">
        <div className="text-center mb-24">
          <motion.h2 
            initial={{ opacity: 0, y: -20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5 }}
            className="text-4xl md:text-5xl font-black text-[#3a2e25] mb-4 tracking-tight"
          >
            Ø®Ø±ÙŠØ·Ø© ÙƒÙ†Ø² Ø§Ù„ÙƒÙ„Ù…Ø§Øª
          </motion.h2>
          <motion.p 
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5, delay: 0.2 }}
            className="max-w-2xl mx-auto text-lg text-[#6b523f]"
          >
            Ø§ØªØ¨Ø¹ Ø§Ù„Ù…Ø³Ø§Ø± Ù…Ù† Ù…Ø¨ØªØ¯Ø¦ Ø¥Ù„Ù‰ Ù…Ø­ØªØ±Ù. ÙƒÙ„ Ø¯Ø±Ø³ ÙŠØ¨Ù†ÙŠ Ø¹Ù„Ù‰ Ù…Ø§ Ù‚Ø¨Ù„Ù‡ØŒ Ù„ÙŠØ±Ø´Ø¯Ùƒ Ù†Ø­Ùˆ Ø¥ØªÙ‚Ø§Ù† ÙÙ† Ø§Ù„ÙƒÙˆØ¨ÙŠ Ø±Ø§ÙŠØªÙ†Ø¬.
          </motion.p>
        </div>
        
        <div ref={journeyRef} className="relative">
          <div className="absolute left-1/2 top-0 h-full w-1 -translate-x-1/2 hidden md:block" 
            style={{
                backgroundImage: 'linear-gradient(to bottom, #8c6b52 50%, transparent 50%)',
                backgroundSize: '1px 15px'
            }}
          >
             <motion.div style={{ scaleY: lineHeight, originY: 0 }} className="h-full w-full bg-[#3a2e25]"/>
          </div>

          <div className="space-y-16 md:space-y-0">
            {lessonsData.map((lesson, index) => {
              const isCompleted = completedLessons.has(lesson.id);
              const isEven = index % 2 === 0;
              const cardVariants = {
                hidden: { opacity: 0, x: isEven ? -100 : 100, scale: 0.9 },
                visible: { 
                  opacity: 1, 
                  x: 0, 
                  scale: 1,
                  transition: { duration: 0.6, ease: [0.6, 0.05, 0.01, 0.9] } 
                },
              };

              return (
                <div key={lesson.id} className="md:flex md:items-center md:justify-center md:mb-24 relative">
                  <motion.div
                    variants={cardVariants}
                    initial="hidden"
                    whileInView="visible"
                    viewport={{ once: true, amount: 0.5 }}
                    className={`w-full md:w-5/12 ${isEven ? 'md:pr-8' : 'md:pl-8'} ${isEven ? '' : 'md:order-last'}`}
                  >
                    <motion.div
                      whileHover={{ y: -8, scale: 1.02 }}
                      transition={{ type: "spring", stiffness: 400, damping: 10 }}
                      className="bg-[#e9dfc7]/70 border-2 border-[#8c6b52] rounded-lg p-6 shadow-xl backdrop-blur-sm group"
                    >
                      <div className="flex items-center justify-end text-right space-x-4 space-x-reverse">
                        <div className="text-right">
                          <div className="flex items-center justify-end space-x-3 space-x-reverse">
                            <h3 className="text-xl font-bold text-[#3a2e25]">Ø§Ù„Ø¯Ø±Ø³ {lesson.id}</h3>
                            {isCompleted && (
                              <span className="flex items-center text-xs font-semibold bg-green-700/20 text-green-800 px-2 py-0.5 rounded-full">
                                <Icon type="Check" className="w-3 h-3 ml-1"/> Ù…ÙƒØªÙ…Ù„
                              </span>
                            )}
                          </div>
                          <p className="text-[#5a4a3e] font-medium">{lesson.title}</p>
                        </div>
                        <div className="flex-shrink-0 bg-[#f3e9d2] w-16 h-16 rounded-full flex items-center justify-center border-2 border-[#8c6b52] group-hover:border-amber-500 group-hover:scale-110 transition-all duration-300">
                          <Icon type={lesson.icon} className="w-8 h-8 text-[#6b523f]" />
                        </div>
                      </div>
                      <p className="mt-4 text-[#6b523f] text-right">{lesson.summary}</p>
                      <div className="mt-6 text-left">
                        <button
                          onClick={() => handleStartLesson(lesson)}
                          disabled={lesson.icon === "Clock"}
                          className="bg-[#3a2e25] text-amber-50 font-semibold py-2 px-5 rounded-lg shadow-md hover:bg-[#5a4a3e] disabled:bg-slate-800 disabled:text-slate-500 disabled:cursor-not-allowed focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-offset-[#f3e9d2] focus:ring-[#8c6b52] transition-all duration-200 transform group-hover:scale-105"
                        >
                          Ø§ÙƒØªØ´Ù Ø§Ù„Ø³Ø±
                        </button>
                      </div>
                    </motion.div>
                  </motion.div>
                  
                  <div className="hidden md:block absolute top-1/2 left-1/2 -translate-y-1/2 -translate-x-1/2">
                    <motion.div 
                      initial={{ scale: 0 }}
                      whileInView={{ scale: 1 }}
                      transition={{ type: 'spring', stiffness: 500, damping: 30, delay: 0.2 }}
                      viewport={{ once: true, amount: 0.8 }}
                      className={`mx-auto w-6 h-6 rounded-full ${isCompleted ? 'bg-amber-400' : 'bg-[#8c6b52]'} border-4 border-[#f3e9d2] ring-4 ${isCompleted ? 'ring-amber-400/30' : 'ring-[#8c6b52]/30'}`}
                    />
                  </div>
                  
                  <div className="hidden md:block w-5/12"></div>
                </div>
              );
            })}
          </div>
        </div>
      </main>

      {allLessonsCompleted && <CompletionModal studentName={studentName} onRestart={handleRestartJourney} />}
      
      <DesertFog />

      <AnimatePresence>
        {activeLesson && (
          <LessonModal
            lesson={activeLesson}
            isCompleted={completedLessons.has(activeLesson.id)}
            onClose={handleCloseModal}
            onComplete={handleCompleteLesson}
            onUncomplete={handleUncompleteLesson}
          />
        )}
      </AnimatePresence>
    </div>
  );
}

